mapper.select:
SELECT 
NAME, GROUPID, NICKNAME
FROM user
WHERE 1=1
AND ID = #{id}
AND PASSWORD = #{password}



SELECT BIRTH
FROM user
WHERE 1=1
AND ID = #{id}
AND NAME = #{name}
AND BIRTH = #{birth}

mapper.insert:

UPDATE user
SET PASSWORD = #{password, jdbcType=VARCHAR}
, NICKNAME = #{nickname, jdbcType=VARCHAR}
, indate = SYSDATE()
WHERE birth = #{birth, jdbcType=VARCHAR}

