mapper.select:
SELECT A.IDX as idx , A.TITLE as title , A.CONTENTS as contents , IFNULL(A.COUNT,0) as count , A.WRITER as writer , A.INDATE as indate , B.NAME as name , (SELECT COUNT(*) FROM board_reply X WHERE X.IDX=A.IDX) AS reply FROM board A LEFT JOIN user B ON A.WRITER = B.ID WHERE 1=1 AND BOARDID = #{boardId}
AND (A.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')) OR A.CONTENTS LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY A.IDX DESC LIMIT #{firstIndex}, #{recordCountPerPage}

mapper.update:
UPDATE board 
SET TITLE=#{title} 
, CONTENTS=#{contents, jdbcType=VARCHAR}
WHERE IDX=#{idx}



UPDATE board
SET COUNT = IFNULL(COUNT, 0)+1
WHERE IDX=#{idx}

mapper.select:
SELECT A.IDX AS idx ,A.TITLE AS title ,A.CONTENTS AS contents ,IFNULL(A.COUNT,0) AS count ,A.WRITER AS writer ,A.INDATE AS indate ,A.BOARDID AS boardId ,B.NICKNAME AS nickName ,B.GROUPID AS groupId ,(SELECT COUNT(*) FROM board_reply X WHERE X.idx=A.idx) AS reply FROM board A LEFT JOIN user B ON A.WRITER = B.ID WHERE 1=1 AND A.BOARDID = #{boardId} AND B.GROUPID = #{groupId}
AND(A.TITLE LIKE CONCAT('%',#{searchKeyword},'%')) OR A.CONTENTS LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY A.IDX DESC LIMIT #{firstIndex}, #{recordCountPerPage}
